import React, { useState } from "react";

function Verify() {
  const [certId, setCertId] = useState("");
  const [result, setResult] = useState(null);
  const [error, setError] = useError("");

  const handleVerify = async () => {
    if (!certId) {
      setError("Please enter Certificate ID");
      return;
    }

    try {
      const token = localStorage.getItem("adminToken"); // JWT stored earlier

      const res = await fetch(
        `http://localhost:5000/api/admin/certificates/${certId}/verify`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify({ status: "verified" }),
        }
      );

      const data = await res.json();

      if (!res.ok) {
        setError(data.error || "Something went wrong");
        setResult(null);
      } else {
        setError("");
        setResult(data);
      }
    } catch (err) {
      setError("Server error");
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Verify Certificate</h2>

      <input
        type="text"
        placeholder="Enter Certificate ID"
        value={certId}
        onChange={(e) => setCertId(e.target.value)}
        style={{ padding: "10px", width: "250px" }}
      />

      <button onClick={handleVerify} style={{ marginLeft: "10px" }}>
        Verify
      </button>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {result && (
        <div style={{ marginTop: "20px" }}>
          <h3>{result.message}</h3>

          <pre>{JSON.stringify(result, null, 2)}</pre>
        </div>
      )}
    </div>
  );
}

export default Verify;
