import React, { useState } from 'react';
import axios from 'axios';

const UploadCertificate = ({ onUpload }) => {
  const [file, setFile] = useState(null);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

const handleSubmit = async (e) => {
  e.preventDefault();
  setError(null);
  setSuccess(null);

  if (!file) return setError("Please select a file");

  const formData = new FormData();
  formData.append("file", file);
  formData.append("title", title);
  formData.append("description", description);

  try {
    const token = "<PASTE_YOUR_JWT_HERE>"; // for testing
    const res = await axios.post(
      "http://localhost:5000/api/user/certificates",
      formData,
      {
        headers: { Authorization: `Bearer ${token}` },
      }
    );

    setSuccess("Certificate uploaded successfully!");
    setFile(null);
    setTitle("");
    setDescription("");

    if (onUpload) onUpload();
  } catch (err) {
    console.error("Upload error:", err.response || err.message);
    setError(err.response?.data?.error || "File is not uploaded");
  }
};


  return (
    <div>
      <h2>Upload Certificate</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {success && <p style={{ color: 'green' }}>{success}</p>}
      <form onSubmit={handleSubmit}>
        <input type="text" placeholder="Title" value={title} onChange={e => setTitle(e.target.value)} />
        <input type="text" placeholder="Description" value={description} onChange={e => setDescription(e.target.value)} />
        <input type="file" onChange={handleFileChange} />
        <button type="submit">Upload</button>
      </form>
    </div>
  );
};


export default UploadCertificate;
