import React, { useState } from "react";
import axios from "axios";
import "./VerifyCertificate.css";

const VerifyCertificate = () => {
  const [certificateId, setCertificateId] = useState("");
  const [certificate, setCertificate] = useState(null);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const handleVerify = async () => {
    if (!certificateId.trim()) {
      setError("Please enter a Certificate ID");
      return;
    }

    setError("");
    setLoading(true);
    setCertificate(null);

    try {
      const res = await axios.get(`http://localhost:5000/api/certificates/${certificateId}`);
      setCertificate(res.data);
    } catch (err) {
      setError("Certificate not found or invalid ID");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="verify-page">
      <div className="verify-card">
        <h2>ðŸŽ“ Certificate Verification</h2>
        <p>Enter the Certificate ID below to verify authenticity.</p>

        <div className="verify-input-section">
          <input
            type="text"
            placeholder="Enter Certificate ID"
            value={certificateId}
            onChange={(e) => setCertificateId(e.target.value)}
          />
          <button onClick={handleVerify}>Verify</button>
        </div>

        {loading && <p className="loading">Checking...</p>}
        {error && <p className="error">{error}</p>}

        {certificate && (
          <div className="result-card">
            <h3>âœ… Certificate Verified</h3>
            <p><strong>Certificate ID:</strong> {certificate._id}</p>
            <p><strong>Title:</strong> {certificate.title}</p>
            <p><strong>Issued To:</strong> {certificate.user_name}</p>
            <p><strong>Email:</strong> {certificate.user_email}</p>
            <p><strong>Status:</strong> {certificate.status}</p>
            <p><strong>Issued On:</strong> {new Date(certificate.uploaded_at).toLocaleDateString()}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default VerifyCertificate;
