import React, { useState, useContext } from "react";
import axios from "axios";
import { AuthContext } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";

const VerifyCertificate = () => {
  const [certificateId, setCertificateId] = useState("");
  const [certificate, setCertificate] = useState(null);
  const [message, setMessage] = useState("");
  const { token } = useContext(AuthContext);
  const navigate = useNavigate();

  // ✅ Correct: Admin can only search via /api/admin/certificates
  const handleSearch = async () => {
    try {
      const res = await axios.get(
        "http://127.0.0.1:5000/api/admin/certificates",
        { headers: { Authorization: `Bearer ${token}` } }
      );

      const found = res.data.find(c => c._id === certificateId);

      if (!found) {
        setCertificate(null);
        setMessage("Certificate not found or invalid ID.");
        return;
      }

      setCertificate(found);
      setMessage("");

    } catch (error) {
      console.log(error);
      setMessage("Certificate not found or invalid ID.");
    }
  };

  // ✅ Verify or Reject
  const handleVerify = async (status) => {
    try {
      const res = await axios.put(
        `http://127.0.0.1:5000/api/admin/certificates/${certificateId}/verify`,
        { status },
        {
          headers: { Authorization: `Bearer ${token}` }
        }
      );

      setMessage(res.data.message);
      setCertificate(prev => ({ ...prev, status }));
    } catch (error) {
      console.log(error);
      setMessage("Failed to update certificate status.");
    }
  };

  return (
    <div className="dashboard-container">
      <h2>Verify Certificate</h2>

      <input
        type="text"
        placeholder="Enter Certificate ID"
        value={certificateId}
        onChange={(e) => setCertificateId(e.target.value)}
        style={{ padding: "10px", width: "300px", marginRight: "10px" }}
      />

      <button onClick={handleSearch}>Search</button>

      {message && <p>{message}</p>}

      {certificate && (
        <div style={{ marginTop: "20px" }}>
          <h4>Certificate Details</h4>
          <p><strong>Title:</strong> {certificate.title}</p>
          <p><strong>Status:</strong> {certificate.status}</p>

          <button
            onClick={() => handleVerify("verified")}
            style={{
              backgroundColor: "green",
              color: "white",
              padding: "8px 16px",
              marginRight: "10px",
              border: "none",
              borderRadius: "6px",
              cursor: "pointer"
            }}
          >
            Verify
          </button>

          <button
            onClick={() => handleVerify("rejected")}
            style={{
              backgroundColor: "red",
              color: "white",
              padding: "8px 16px",
              border: "none",
              borderRadius: "6px",
              cursor: "pointer"
            }}
          >
            Reject
          </button>
        </div>
      )}

      <button
        onClick={() => navigate("/admin-dashboard")}
        style={{
          marginTop: "20px",
          background: "none",
          color: "blue",
          border: "none",
          textDecoration: "underline",
          cursor: "pointer"
        }}
      >
        ← Back
      </button>
    </div>
  );
};

export default VerifyCertificate;
