import React, { useEffect, useState, useContext } from "react";
import { getAllCertificates, verifyCertificate } from "../api";
import { AuthContext } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";
import "./Dashboard.css";

const VerifyCertificate = () => {
  const { token, user } = useContext(AuthContext);
  const [certificates, setCertificates] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    if (!user || user.role !== "admin") {
      navigate("/dashboard");
      return;
    }
    fetchCertificates();
  }, [user, token]);

  const fetchCertificates = async () => {
    try {
      setLoading(true);
      const response = await getAllCertificates(token);
      setCertificates(response.data);
      setError("");
    } catch (err) {
      console.error("Error fetching certificates:", err);
      setError("Failed to load certificates");
    } finally {
      setLoading(false);
    }
  };

  const handleVerify = async (certId, status) => {
    const confirmAction = window.confirm(
      `Are you sure you want to mark this certificate as ${status}?`
    );
    if (!confirmAction) return;

    try {
      await verifyCertificate(certId, status, token);
      fetchCertificates(); // refresh list after update
    } catch (err) {
      console.error("Error verifying certificate:", err);
      setError("Failed to verify certificate");
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString("en-IN", {
      year: "numeric",
      month: "short",
      day: "numeric",
      hour: "2-digit",
      minute: "2-digit",
    });
  };

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h2>Certificate Verification</h2>
        <p>Admin: {user?.name}</p>
      </div>

      <button
        onClick={() => navigate("/admin-dashboard")}
        style={{
          backgroundColor: "white",
          color: "purple",
          border: "1px solid purple",
          borderRadius: "8px",
          padding: "10px 20px",
          cursor: "pointer",
          marginBottom: "20px",
        }}
      >
        ⬅ Back to Admin Dashboard
      </button>

      {loading ? (
        <div className="loading-message">
          <p>Loading certificates...</p>
        </div>
      ) : error ? (
        <div className="error-message">
          <p>{error}</p>
        </div>
      ) : certificates.length === 0 ? (
        <div className="no-certificates">
          <p>No certificates available for verification.</p>
        </div>
      ) : (
        <div className="table-container">
          <table className="certificates-table">
            <thead>
              <tr>
                <th>Title</th>
                <th>User</th>
                <th>File Name</th>
                <th>Upload Date</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {certificates.map((cert) => (
                <tr key={cert._id}>
                  <td>{cert.title || "Untitled"}</td>
                  <td>
                    <strong>{cert.user_name}</strong>
                    <br />
                    <small>{cert.user_email}</small>
                  </td>
                  <td>{cert.file_name}</td>
                  <td>{formatDate(cert.uploaded_at)}</td>
                  <td>
                    <span className={`status-badge status-${cert.status}`}>
                      {cert.status}
                    </span>
                  </td>
                  <td>
                    {cert.status === "pending" ? (
                      <>
                        <button
                          className="btn-approve"
                          onClick={() => handleVerify(cert._id, "verified")}
                        >
                          ✅ Verify
                        </button>
                        <button
                          className="btn-reject"
                          onClick={() => handleVerify(cert._id, "rejected")}
                        >
                          ❌ Reject
                        </button>
                      </>
                    ) : (
                      <span className="status-text">
                        {cert.status === "verified" ? "Approved" : "Rejected"}
                      </span>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default VerifyCertificate;
